            itemBuilder: (context, index) {
              final item = _data[_selectedCategory]![index];
              return Card(
                child: InkWell(
                  onTap: () {
                    if (_selectedCategory == 'Étudiants') {
                      _showStudentDetails(item['name']);
                    } else if (_selectedCategory == 'Cours') {
                      _showCourseDetails(item['name']);
                    } else if (_selectedCategory == 'Quiz') {
                      _showQuizDetails(item['name']);
                    }
                  },
