  Widget _buildCategoryIcon(String category) {
    IconData iconData;
    Color color;
    switch (category) {
      case 'Cours':
        iconData = Icons.book;
        color = Colors.blue;
        break;
      case 'Quiz':
        iconData = Icons.question_mark;
        color = Colors.green;
        break;
      case 'Étudiants':
        iconData = Icons.group;
        color = Colors.orange;
        break;
      default:
        iconData = Icons.error;
        color = Colors.black;
    }
    return Icon(
      iconData,
      color: color,
      size: 40,
    );
  }

  void _createNewItem(String category) {
    switch (category) {
      case 'Cours':
        _createNewCourse();
        break;
      case 'Quiz':
        _createNewQuiz();
        break;
      case 'Étudiants':
        _createNewStudent();
        break;
      default:
        break;
    }
  }

