  void _createNewQuiz() async {
    final TextEditingController quizTitleController = TextEditingController();

    await showDialog(
      context: context,
      builder: (BuildContext context) {
        return AlertDialog(
          title: Text('Créer un nouveau quiz'),
          content: Column(
            children: [
              TextField(
                controller: quizTitleController,
                decoration: InputDecoration(
                  hintText: 'Entrez le titre du quiz',
                ),
              ),
              SizedBox(height: 10),
              ElevatedButton(
                onPressed: getImage,
                child: Text('Télécharger une image'),
              ),
            ],
          ),
          actions: [
            TextButton(
              onPressed: () {
                Navigator.of(context).pop();
              },
              child: Text('Annuler'),
            ),
            ElevatedButton(
              onPressed: () {
                if (quizTitleController.text.isNotEmpty && _image != null) {
                  setState(() {
                    _data['Quiz']?.add({
                      'name': quizTitleController.text,
                      'image': _image!.path, // Remplacer par l'URL de l'image
                    });
                  });
                }
                Navigator.of(context).pop();
              },
              child: Text('Enregistrer'),
            ),
          ],
        );
      },
    );
  }

  void _createNewStudent() async {
    String? nom;
    String? prenom;

    await showDialog(
      context: context,
      builder: (BuildContext context) {
        return AlertDialog(
          title: Text('Ajouter un nouvel étudiant'),
          content: Column(
            mainAxisSize: MainAxisSize.min,
            children: [
              TextField(
                onChanged: (value) {
                  nom = value;
                },
                decoration: InputDecoration(
                  hintText: 'Nom',
                ),
              ),
              SizedBox(height: 10),
              TextField(
                onChanged: (value) {
                  prenom = value;
                },
                decoration: InputDecoration(
                  hintText: 'Prénom',
                ),
              ),
              SizedBox(height: 10),
              DropdownButton<String>(
                value: _selectedNiveau,
                onChanged: (String? newValue) {
                  setState(() {
                    _selectedNiveau = newValue!;
                  });
                },
                items: _niveaux.map<DropdownMenuItem<String>>((String value) {
                  return DropdownMenuItem<String>(
                    value: value,
                    child: Text(value),
                  );
                }).toList(),
              ),
            ],
          ),
          actions: [
            TextButton(
              onPressed: () {
                Navigator.of(context).pop();
              },
              child: Text('Annuler'),
            ),
            ElevatedButton(
              onPressed: () {
                if (nom != null && prenom != null) {
                  setState(() {
                    _data['Étudiants']?.add({
                      'name': '$nom $prenom ($_selectedNiveau)',
                      'image':
                          'URL_de_l_image', // Remplacer par l'URL de l'image
                    });
                  });
                }
                Navigator.of(context).pop();
              },
              child: Text('Enregistrer'),
            ),
          ],
        );
      },
    );
  }

  void _createNewCourse() async {
    String? nom;
    String? duree;
    File? pdfFile;

    await showDialog(
      context: context,
      builder: (BuildContext context) {
        return AlertDialog(
          title: Text('Ajouter un nouveau cours'),
          content: Column(
            mainAxisSize: MainAxisSize.min,
            children: [
              TextField(
                onChanged: (value) {
                  nom = value;
                },
                decoration: InputDecoration(
                  hintText: 'Nom du cours',
                ),
              ),
              SizedBox(height: 10),
              TextField(
                onChanged: (value) {
                  duree = value;
                },
                decoration: InputDecoration(
                  hintText: 'Durée (en heures)',
                ),
              ),
              SizedBox(height: 10),
              ElevatedButton(
                onPressed: () async {
                  FilePickerResult? result =
                      await FilePicker.platform.pickFiles(
                    type: FileType.custom,
                    allowedExtensions: ['pdf'],
                  );
                  if (result != null) {
                    pdfFile = File(result.files.single.path!);
                  }
                },
                child: Text('Sélectionner un PDF'),
              ),
            ],
          ),
          actions: [
            TextButton(
              onPressed: () {
                Navigator.of(context).pop();
              },
              child: Text('Annuler'),
            ),
            ElevatedButton(
              onPressed: () {
                if (nom != null && duree != null && pdfFile != null) {
                  setState(() {
                    _data['Cours']?.add({
                      'name': '$nom - Durée: $duree heures',
                      'image':
                          'URL_de_l_image', // Remplacer par l'URL de l'image
                    });
                  });
                }
                Navigator.of(context).pop();
              },
              child: Text('Enregistrer'),
            ),
          ],
        );
      },
    );
  }
